/**
 * @param {string} selector
 * @param {Node} [node]
 * @returns {boolean}
 */
export function matches(selector: string, node?: Node): boolean
/**
 * @param {string} selector
 * @param {Node} [node]
 * @returns {Node|null}
 */
export function select(selector: string, node?: Node): Node | null
/**
 * @param {string} selector
 * @param {Node} [node]
 * @returns {Array.<Node>}
 */
export function selectAll(selector: string, node?: Node): Array<Node>
export type Node = import('unist').Node
